package com.core;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
		for(int i=1;i<=5;i++)
		{
			System.out.println("keerthi");
		}
		
		for(int i=1;i<=10;i++)
		{
			System.out.println(i);
		}
		
		for( int i=7; i>=4; i--)
		{ 
			System.out.println(i);
		}
		
       for(int i=1; i<=10;i=i+2)
       {
    	   System.out.println(i);
       }
       
       for(int i=0; i<=10; i=i+2)
       {
    	   System.out.println(i);
       }
       
       for(int i=5; i<=60; i=i+5)
       {
    	   System.out.println(i);
       }
       for(int i=1;i<=5;i++)
       {
    	   System.out.println("&&&");
    	   
    	   
       }
       
       for(int i=1;i<=3;i++)
       {
    	   System.out.println("*");
       }
       }
	}


