package com.core;

public class SelectiveStatement1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=5600;
		int b=780;
		int c=300;
		if((a>b)&&(a>c))
		{
		System.out.println("A is big");
		}
		else if(b>c)
		{
			System.out.println("B is big");
		}
		else
		{
			System.out.println("C is big");
		}
		
	}

}
